mapper.select:
select o.id, o.number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time, o.pay_method, o.pay_status, o.amount, o.remark, o.user_name, o.phone, o.address, o.consignee, o.cancel_reason, o.rejection_reason, o.cancel_time, o.estimated_delivery_time, o.delivery_status, o.delivery_time, o.pack_amount, o.tableware_number, o.tableware_status, od.id as order_detail_id, od.name, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor, od.number as order_detail_number, od.amount as order_detail_amount, od.image from orders as o left join order_detail as od on o.id = od.order_id where o.user_id = #{userId}
and o.status = #{status}
order by o.order_time desc

select o.id, o.number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time, o.pay_method, o.pay_status, o.amount, o.remark, o.user_name, o.phone, o.address, o.consignee, o.cancel_reason, o.rejection_reason, o.cancel_time, o.estimated_delivery_time, o.delivery_status, o.delivery_time, o.pack_amount, o.tableware_number, o.tableware_status, od.id as order_detail_id, od.name, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor, od.number as order_detail_number, od.amount as order_detail_amount, od.image from orders as o left join order_detail as od on o.id = od.order_id
and o.status = #{status}
and o.order_time >= #{beginTime}
and o.order_time <= #{endTime}
and o.phone like concat('%',#{phone},'%')
and o.number like concat('%',#{number},'%')
order by o.order_time desc

select count(*)
from orders
where status = #{status}

select o.id, o.number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time, o.pay_method, o.pay_status, o.amount, o.remark, o.user_name, o.phone, o.address, o.consignee, o.cancel_reason, o.rejection_reason, o.cancel_time, o.estimated_delivery_time, o.delivery_status, o.delivery_time, o.pack_amount, o.tableware_number, o.tableware_status from orders as o
and o.status = #{status}
and o.order_time >= #{beginTime}
and o.order_time <= #{endTime}
and o.phone like concat('%',#{phone},'%')
and o.number like concat('%',#{number},'%')
order by o.order_time desc

select *
from orders
where order_time <=  #{outOfTime}
and status = #{status}

