select sum(amount)
from orders
where status = 5

    and order_time >= #{begin}


    and order_time <= #{end}

select od.name as name, sum(od.number) as number from orders as o inner join order_detail as od on o.id = od.order_id where o.status = 5
and o.order_time >= #{begin}
and o.order_time <= #{end}
group by od.name order by number desc limit 10
