mapper.select:
select setmeal.id, setmeal.category_id, setmeal.name, setmeal.price, setmeal.status, setmeal.description, setmeal.image, setmeal.update_time, category.name as categoryName from setmeal left join category on setmeal.category_id = category.id
and setmeal.category_id = #{categoryId}
and setmeal.name like concat('%',#{name},'%')
and setmeal.status = #{status}
